import React, { Component } from 'react';
import PropTypes from 'prop-types';

import {
  addSong,
  removeSong,
  getFavoriteSongs,
} from '../services/favoriteSongsAPI';
import Loading from './Loading';

export default class MusicCard extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      favorites: [],
    };

    this.handleCange = this.handleCange.bind(this);
    this.setFavorites = this.setFavorites.bind(this);
  }

  async handleCange(event) {
    console.log('teste');
    const { checked } = event.target;
    const { music, updateFavorites } = this.props;

    this.setState({
      loading: true,
    });

    if (checked) {
      await addSong(music);
    } else removeSong(music);
    // checked ? await addSong(music) : removeSong(music);

    const favorites = await getFavoriteSongs();

    // this.setState(
    //   {
    //     loading: false,
    //     favorites,
    //   },
    //   () => (updateFavorites !== undefined ? updateFavorites() : null)
    // );

    this.setState(
      {
        loading: false,
        favorites,
      },
      () => (updateFavorites !== undefined ? updateFavorites() : null)
    );
  }

  async setFavorites() {
    const favorites = await getFavoriteSongs();
    this.setState({
      favorites,
    });
  }

componentDidMount(){
  this.setFavorites();
}

  render() {
    const { music } = this.props;

    const { trackName, previewUrl, trackId } = music;

    const { favorites, loading } = this.state;
    return previewUrl !== undefined ? (
      <div>
        <p>{trackName}</p>
        <audio data-testid="audio-component" src={previewUrl} controls>
          <track kind="captions" />O seu navegador não suporta o elemento{' '}
          <code>audio</code>.
        </audio>
        <label htmlFor={trackId}>
          Favorita
          <input
            type="checkbox"
            data-testid={`checkbox-music-${trackId}`}
            onChange={this.handleCange}
            checked={favorites.some((song) => song.trackId === trackId)}
            id={trackId}
          />
        </label>

        {loading ? <Loading /> : null}
      </div>
    ) : null;
  }
}

MusicCard.propTypes = {
  music: PropTypes.arrayOf(Object).isRequired,
};
