import React, { Component } from 'react';
import Header from '../components/Header';
import Loading from '../components/Loading';
import { getUser } from '../services/userAPI';

export default class ProfileEdit extends Component {
  constructor() {
    super();

    this.state = {
      loading: false,
      profile: [],
      profileName: '',
      profileImage: '',
      profilEmail: '',
      profileDescription: '',
    };

    this.loadProfile = this.loadProfile.bind(this);
    this.buttonValidation = this.buttonValidation.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.loadProfile();
  }

  async loadProfile() {
    this.setState({
      loading: true,
    });

    const profile = await getUser();
    this.setState({
      profile,
      loading: false,
    });
  }

  buttonValidation() {
    const { profile } = this.state;
    console.log(Object.values(profile).every((value) => value.length > 0));
    return Object.values(profile).every((value) => value.length > 0);
  }

  handleChange(event) {
    const { value } = event;
    console.log(value);
  }

  render() {
    const {
      loading,
      profile,
      profilEmail,
      profileDescription,
      profileImage,
      profileName,
    } = this.state;

    return (
      <div data-testid="page-profile-edit">
        <Header />
        {loading ? (
          <Loading />
        ) : (
          <div>
            <input
              type="text"
              data-testid="edit-input-name"
              onChange={this.handleChange}
              value={profileName}
            ></input>

            <input
              type="email"
              data-testid="edit-input-email"
              onChange={this.handleChange}
              value={profi}
            ></input>

            <input
              type="text"
              data-testid="edit-input-description"
              onChange={this.handleChange}
            ></input>

            <input
              type="text"
              data-testid="edit-input-image"
              onChange={this.handleChange}
            ></input>
            {this.buttonValidation() ? (
              <button type="button" data-testid="edit-button-save">
                Salvar
              </button>
            ) : null}
          </div>
        )}
      </div>
    );
  }
}
