import React, { Component } from 'react';
import { Redirect } from 'react-router';
import Header from '../components/Header';
import Loading from '../components/Loading';
import { getUser, updateUser } from '../services/userAPI';

export default class ProfileEdit extends Component {
  constructor() {
    super();

    this.state = {
      loading: false,
      profile: [],
      redirect: false,
    };

    this.loadProfile = this.loadProfile.bind(this);
    this.buttonValidation = this.buttonValidation.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.loadProfile();
  }

  async loadProfile() {
    this.setState({
      loading: true,
    });

    const profile = await getUser();
    this.setState({
      profile,
      loading: false,
    });
  }

  buttonValidation() {
    const { profile } = this.state;
    let result = false;
    let email = Object.values(profile)[1];
    result = Object.values(profile).every((value) => value.length > 0);

    if (email !== undefined) {
      if (email.includes('@') && email.includes('.com')) {
        result = true;
      } else result = false;
    }

    return result;
  }

  handleChange(event) {
    const { profile } = this.state;

    const { value, id } = event.target;
    this.setState({
      profile: { ...profile, [id]: value },
    });
  }

  render() {
    const {
      loading,
      profile,
      profilEmail,
      profileDescription,
      profileImage,
      profileName,
      redirect,
    } = this.state;

    return (
      <div data-testid="page-profile-edit">
        <Header />
        {loading ? (
          <Loading />
        ) : (
          <form autoComplete="off">
            <input
              type="text"
              data-testid="edit-input-name"
              onChange={this.handleChange}
              value={profile.name}
              id="name"
            ></input>

            <input
              type="email"
              data-testid="edit-input-email"
              onChange={this.handleChange}
              value={profile.email}
              id="email"
            ></input>

            <input
              type="text"
              data-testid="edit-input-description"
              onChange={this.handleChange}
              value={profile.description}
              id="description"
            ></input>

            <input
              type="text"
              data-testid="edit-input-image"
              onChange={this.handleChange}
              value={profile.image}
              id="image"
            ></input>
            {this.buttonValidation() ? (
              <button
                type="button"
                data-testid="edit-button-save"
                onClick={
                  (() => updateUser(this.state.profile), this.setState({
                    redirect: true,
                  })
                  (<Redirect to="/profile" />))
                }
              >
                Salvar
              </button>
            ) : (
              <button type="button" data-testid="edit-button-save" disabled>
                Salvar
              </button>
            )}
          </form>
        )}
      </div>
    );
  }
}
