import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Loading from '../components/Loading';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

export default class Search extends Component {
  constructor() {
    super();

    this.state = {
      inputSearch: '',
      currentSearch: '',
      searchResult: undefined,
      pesquisou: false,
      loading: false,
    };

    this.handleChange = this.handleChange.bind(this);
    this.searchAlbum = this.searchAlbum.bind(this);
  }

  handleChange(event) {
    const { id, value } = event.target;
    this.setState({
      [id]: value,
    });
  }

  async searchAlbum() {
    const album = this.state.inputSearch;

    this.setState({
      inputSearch: '',
      currentSearch: album,
      loading: true,
    });

    const searchResult = await searchAlbumsAPI(album);

    this.setState({
      searchResult: searchResult,
      loading: false,
      pesquisou: true,
    });
  }

  render() {
    const { inputSearch, loading, searchResult, currentSearch, pesquisou } =
      this.state;
    return (
      <div data-testid="page-search">
        <Header />
        {loading ? (
          <Loading />
        ) : (
          <form>
            <input
              type="text"
              data-testid="search-artist-input"
              onChange={this.handleChange}
              value={this.state.inputSearch}
              id="inputSearch"
            />
            {inputSearch.length >= 2 ? (
              <button
                data-testid="search-artist-button"
                onClick={this.searchAlbum}
              >
                Pesquisar
              </button>
            ) : (
              <button data-testid="search-artist-button" disabled>
                Pesquisar
              </button>
            )}
          </form>
        )}
        {pesquisou ? (
          searchResult.length > 0 ? (
            <div>
              <p>Resultado de álbuns de: {currentSearch}</p>
              {searchResult.map((album) => (
                <Link
                  to={`/album${album.collectionId}`}
                  data-testid={`link-to-album-${album.collectionId}`}
                >
                  {album.collectionName}
                </Link>
              ))}
            </div>
          ) : (
            'Nenhum álbum foi encontrado'
          )
        ) : null}
      </div>
    );
  }
}
