import React, { Component } from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';
import Loading from '../components/Loading';

export default class Album extends Component {
  constructor() {
    super();

    this.state = {
      musics: undefined,
      loading: false,
      favorite_songs: [],
    };
    this.isFavorite = this.isFavorite.bind(this);
    // this.setMusics = this.setMusics.bind(this);
  }

  async componentDidMount() {
    const { match } = this.props;
    const { params } = match;
    const { id } = params;
    const musics = await getMusics(id);

    this.setState({
      musics
    });

    this.setState({
      loading: true,
    });

    const favorites = await getFavoriteSongs();

    this.setState({
      favorite_songs: favorites,
      loading: false,
    });
  }

  isFavorite(trackId) {
    console.log(trackId);
    return this.state.favorite_songs
      ? this.state.favorite_songs.some((music) => music.trackId === trackId)
      : false;
  }

  render() {
    // this.setMusics();
    const { musics } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        {musics !== undefined ? (
          <div>
            <p data-testid="artist-name">{musics[0].artistName}</p>
            <p data-testid="album-name">
              {musics[0].collectionName} {musics[0].artistName}
            </p>
            {musics.map((music) => (
              <MusicCard music={music} favorites={this.state.favorite_songs} />
            ))}
            {this.state.loading ? <Loading /> : null}
          </div>
        ) : null}
      </div>
    );
  }
}
