import React, { Component } from 'react';
import { addSong, removeSong } from '../services/favoriteSongsAPI';
import Loading from './Loading';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';

export default class MusicCard extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      favorites: [],
    };

    this.handleCange = this.handleCange.bind(this);
  }

  async handleCange(event) {
    const { checked } = event.target;

    this.setState({
      loading: true,
    });

    checked ? await addSong(this.props.music) : removeSong(this.props.music);

    const favorites = await getFavoriteSongs();

    this.setState({
      loading: false,
      favorites: favorites,
    });
  }

  render() {
    const { trackName, previewUrl, trackId } = this.props.music;
    return previewUrl !== undefined ? (
      <div>
        <p>{trackName}</p>
        <audio data-testid="audio-component" src={previewUrl} controls>
          <track kind="captions" />O seu navegador não suporta o elemento{' '}
          <code>audio</code>.
        </audio>
        <label>
          Favorita
          <input
            type="checkbox"
            data-testid={`checkbox-music-${trackId}`}
            onChange={this.handleCange}
            checked={this.state.favorites.some(
              (music) => music.trackId === trackId
            )}
          ></input>
        </label>

        {this.state.loading ? <Loading /> : null}
      </div>
    ) : null;
  }
}
