import React, { Component } from 'react';
import { addSong, getFavoriteSongs } from '../services/favoriteSongsAPI';
import Loading from './Loading';

export default class MusicCard extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
    };

    this.handleCange = this.handleCange.bind(this);
  }

  async handleCange(event) {
    const { checked } = event.target;

    this.setState({
      loading: true,
    });

    checked ? await addSong(this.props.music) : null;

    await getFavoriteSongs();

    this.setState({
      loading: false,
    });
  }

  render() {
    const { trackName, previewUrl, trackId } = this.props.music;
    return previewUrl !== undefined ? (
      <div>
        <p>{trackName}</p>
        <audio data-testid="audio-component" src={previewUrl} controls>
          <track kind="captions" />O seu navegador não suporta o elemento{' '}
          <code>audio</code>.
        </audio>
        {this.props.favorites.some((music) => music.trackId === trackId) ? (
          <label>
            Favorita
            <input
              type="checkbox"
              data-testid={`checkbox-music-${trackId}`}
              onChange={this.handleCange}
              checked="true"
            ></input>
          </label>
        ) : (
          <label>
            Favorita
            <input
              type="checkbox"
              data-testid={`checkbox-music-${trackId}`}
              onChange={this.handleCange}
            ></input>
          </label>
        )}

        {this.state.loading ? <Loading /> : null}
      </div>
    ) : null;
  }
}
