import React, { Component } from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';
import Loading from '../components/Loading';

export default class Album extends Component {
  constructor() {
    super();

    this.state = {
      musics: undefined,
      loading: false,
      favorite_songs: [],
    };
  }

  async componentDidMount() {
    const musics = await getMusics(this.props.match.params.id);
    this.setState({
      musics: musics,
    });

    this.setState({
      laoding: true,
    });

    const favorites = await getFavoriteSongs();

    this.setState({
      favorite_songs: favorites,
      laoding: false,
    });
  }

  isFavorite(trackId){
    console.log(trackId);

    return this.state.favorite_songs.includes(trackId)
  }

  render() {
    const { musics } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        {musics !== undefined ? (
          <div>
            <p data-testid="artist-name">{musics[0].artistName}</p>
            <p data-testid="album-name">
              {musics[0].collectionName} {musics[0].artistName}
            </p>
            {musics.map((music) => (
              <MusicCard music={music} isFavorite={this.isFavorite} />
            ))}
            {this.state.loading ? (
              <Loading  />
            ) : null}
          </div>
        ) : null}
      </div>
    );
  }
}
