import React, { Component } from 'react';
import {
  addSong,
  removeSong,
  getFavoriteSongs,
} from '../services/favoriteSongsAPI';
import Loading from './Loading';

export default class MusicCard extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      favorites: [],
    };

    this.handleCange = this.handleCange.bind(this);
    this.setFavorites = this.setFavorites.bind(this);
  }

  async handleCange(event) {
    const { checked } = event.target;
    const { music } = this.props;

    this.setState({
      loading: true,
    });

    checked ? await addSong(music) : removeSong(music);

    const favorites = await getFavoriteSongs();

    this.setState({
      loading: false,
      favorites: favorites,
    });
  }
  async setFavorites() {
    const favorites = await getFavoriteSongs();

    this.setState({
      favorites: favorites,
    });
  }

  render() {
    this.setFavorites();
    const { music } = this.props;

    const { trackName, previewUrl, trackId } = music;
    return previewUrl !== undefined ? (
      <div>
        <p>{trackName}</p>
        <audio data-testid="audio-component" src={previewUrl} controls>
          <track kind="captions" />
          O seu navegador não suporta o elemento {' '}
          <code>audio</code>.
        </audio>
        <label>
          Favorita
          <input
            type="checkbox"
            data-testid={`checkbox-music-${trackId}`}
            onChange={this.handleCange}
            checked={this.state.favorites.some(
              (music) => music.trackId === trackId
            )}
          ></input>
        </label>

        {this.state.loading ? <Loading /> : null}
      </div>
    ) : null;
  }
}
